package com.env.bean;

public class EP_Application {
	private String AppName;
	private String ApkName;
	private String VerName;
	private int VerCode;
	private String VerInfo;
	/**
	 * @return the appName
	 */
	public String getAppName() {
		return AppName;
	}
	/**
	 * @return the apkName
	 */
	public String getApkName() {
		return ApkName;
	}
	/**
	 * @return the verName
	 */
	public String getVerName() {
		return VerName;
	}
	/**
	 * @return the verCode
	 */
	public int getVerCode() {
		return VerCode;
	}
	/**
	 * @return the verInfo
	 */
	public String getVerInfo() {
		return VerInfo;
	}
	/**
	 * @param appName the appName to set
	 */
	public void setAppName(String appName) {
		AppName = appName;
	}
	/**
	 * @param apkName the apkName to set
	 */
	public void setApkName(String apkName) {
		ApkName = apkName;
	}
	/**
	 * @param verName the verName to set
	 */
	public void setVerName(String verName) {
		VerName = verName;
	}
	/**
	 * @param verCode the verCode to set
	 */
	public void setVerCode(int verCode) {
		VerCode = verCode;
	}
	/**
	 * @param verInfo the verInfo to set
	 */
	public void setVerInfo(String verInfo) {
		VerInfo = verInfo;
	}
}
